import React, {Component} from 'react'
import Slider from '../../Slider/Slider'
import Button from '../../Button/Buttton'

const Shape = (props) => {

    const setSize = (size) => (
        props.Size(size)
    )
    return(
        <div>
            <Slider size = {props.size}>Size</Slider>
            <Button bttnFun = {this.setTask("Circle")}>Circle</Button>
            <Button bttnFun = {this.setTask("Rectangle")}>Rectangle</Button>
        </div>
        );
    }

export default Shape;
