import React, {Component} from 'react'
import classes from './Canvas.css'
// import { NONAME } from 'dns';

class Canvas extends Component {
    state= {
        mousedown: false,
        startX: 0,
        startY: 0,
        endX: 0,
        endY: 0,
        one_state_back: null,
        current_state: null
    }

    canvas;
    ctx;

    constructor(props) {
        super(props);
        this.canvas = React.createRef();

    }

    componentDidMount(){
        this.canvas = this.refs.canvas;
        this.ctx = this.canvas.getContext("2d");
        console.log(this.canvas.width,this.canvas.height);
        const statei = this.ctx.getImageData(0,0,this.canvas.width,this.canvas.height);
        this.setState({one_state_back: statei, current_state: statei});
        const dpr = window.devicePixelRatio || 1;
        var rect = this.canvas.getBoundingClientRect();
        console.log(rect,dpr);
        // Give the canvas pixel dimensions of their CSS
        // size * the device pixel ratio.
        this.canvas.width = rect.width * dpr;
        this.canvas.height = rect.height * dpr;
        // Scale all drawing operations by the dpr, so you
        // don't have to worry about the difference.
        this.ctx.scale(dpr, dpr);
        // this.ctx.beginPath();
        // this.ctx.moveTo();
        // this.ctx.lineTo("100px,100px");
        // this.ctx.stroke();
    }


    MouseDown = (e) => {
        e.persist();
        const statei = this.ctx.getImageData(0,0,this.canvas.width,this.canvas.height);
        this.setState(() => ({mousedown : true, startX: e.nativeEvent.offsetX, startY: e.nativeEvent.offsetY, one_state_back: statei}));
        this.activationFunction(e);
    }

    MouseMove = (e) => {
        e.persist();
        if(this.state.mousedown)
        {
            if(this.props.Task == "Line" || this.props.Task == "Circle")
            {
                this.clearCanvas();
            }
            this.activationFunction(e);
        }
    }

    MouseUp = (e) => {
        e.persist();
        this.setState(() => ({mousedown: false, endX: e.nativeEvent.offsetX, endY: e.nativeEvent.offsetY}));
        this.activationFunction(e);
    }

    activationFunction = (e) => {
        if(this.props.Task === "Line")
        {
            return(this.drawLine(e));
        }
        else if(this.props.Task === "Pencil")
        {
            return(console.log("Pencil"));
        }
        else if(this.props.Task === "Shape")
        {
            return(console.log("Shape"));
        }
        else if(this.props.Task === "Erase")
        {
            return(console.log("Erase"));
        }
    }

    clearCanvas = () => {
        this.setState({current_state : this.state.one_state_back});
        this.ctx.clearRect(0,0,500,500);
        // this.ctx.putImageData(this.state.current_state,0,0);

    }

    drawLine = (e) => {
        e.persist();
        this.setState({endX: e.nativeEvent.offsetX, endY: e.nativeEvent.offsetY});
        // console.log(this.state.startX,this.state.startY);	
        // console.log(this.state.endX,this.state.endY);		
        this.ctx.beginPath();
        this.ctx.moveTo(this.state.startX,this.state.startY);
        this.ctx.lineTo(this.state.endX,this.state.endY);
        this.ctx.stroke();
        this.setState({current_state : this.ctx.getImageData(0,0,this.canvas.width,this.canvas.height)});
    }

    // function drawCircle(event,start_point,end_point)
    // {
    //     end_point.X = event.clientX;
    //     end_point.Y = event.clientY;
    //     ctx.beginPath();
    //     ctx.arc(start_point.X,start_point.Y,Distance(start_point,end_point),0,2*Math.PI);
    //     ctx.stroke();
    //     current_state = ctx.getImageData(0,0,c.width,c.height);
    // }

    // function Eraser(event)
    // {	
    //     ctx.clearRect(event.clientX,event.clientY,4,4);
    // }

    // function handLine(event,start_point,end_point)
    // {
    //     start_point.X = end_point.X;
    //     start_point.Y = end_point.Y;
    //     end_point.X = event.clientX;
    //     end_point.Y = event.clientY;
    //     ctx.beginPath();
    //     ctx.moveTo(start_point.X,start_point.Y);
    //     ctx.lineTo(end_point.X,end_point.Y);
    //     ctx.stroke();
    //     current_state = ctx.getImageData(0,0,c.width,c.height);
    // }

    // function clearCanvas(){
    //     current_state = one_state_back;
    //     ctx.clearRect(0,0,500,500);
    //     ctx.putImageData(current_state,0,0);	
    // }

    // function Distance(start_point,end_point)
    // {
    //     var radius = Math.sqrt(Math.pow( start_point.X - end_point.X,2)+Math.pow( start_point.Y - end_point.Y ,2));
    //     return radius;
    // }

 render(){

    return(<canvas 
            ref = "canvas" 
            className= {classes.Canvas} 
            onMouseDown ={this.MouseDown} 
            onMouseMove= {this.MouseMove} 
            onMouseUp={this.MouseUp} ></canvas>);

    }   

}
export default Canvas;
