import React, {Component} from 'react'
import Button from '../../Button/Buttton'

class Erase extends Component {
    state = {
        size: 2
    }

    EraseSizeIncrease = () => {
        this.setState({size : (this.state.size+1)});
    }

    EraseSizeDecrease = () => {
        if(this.state.size > 1)
        this.setState({size : (this.state.size-1)});
    }

    eraser = (e) => {
        e.persist();
        this.ctx.clearRect(e.nativeEvent.offsetX,e.nativeEvent.offsetY,4,4);    
    }

    render(){
    return(
        <div>
            <Button bttnFun = {this.EraseSizeIncrease}> Size+</Button>
            <Button bttnFun = {this.EraseSizeDecrease}> Size-</Button>
        </div>
        );
    }
}

export default Erase;
