const SliderBar = React.createClass({
    propTypes: {
      min: React.PropTypes.number,
      max: React.PropTypes.number,
    },
    getDefaultProps: function() {
      return { min: 0, max: 100 };
    },
    getInitialState: function() {
      return { isDragging: false, value: undefined }
    },
    handleMouseDown(e) {
      this.setState({ isDragging: true });
      e.stopPropagation();
      e.preventDefault;
    },
    handleMouseUp(e) {
      this.setState({ isDragging: false });
      e.stopPropagation();
      e.preventDefault;
    },
    handleMouseMove(e) {
      const { onChange, min, max } = this.props;
      if (this.state.isDragging) {
        const { left, right } = this.rootDOM.getBoundingClientRect();
        const rLeft = left - document.body.getBoundingClientRect().left;

        let value = ((e.clientX - rLeft) / (right - rLeft)) * 100;
        if (value < min) value = min;
        if (value > max) value = max;

        this.setState({ value });
        if (onChange) onChange(value);
      }
      e.stopPropagation();
      e.preventDefault;
    },
    render: function() {
      const { min, max } = this.props;
      const { isDragging } = this.state;
      let { value } = this.state;
      if (typeof value === 'undefined') value = min;

      return (
        <div
          className="component slider-bar"
          ref={c => { this.rootDOM = c; }}
          onMouseUp={this.handleMouseUp}
          onMouseDown={this.handleMouseDown}
          onMouseMove={this.handleMouseMove}
        >
          <div
            className="slider-button"
            style={{ left: value.toString() + "%" }}
          >
            &nbsp;
          </div>
          &nbsp;
        </div>
      );
    },
  });

  const App = React.createClass({
    getInitialState: function() {
      return { displayValue: null };
    },
    handleChange: function(value) {
      this.setState({ displayValue: Math.round(value) });
    },
    render: function() {
      return (
        <div className="demo-app">
          <SliderBar min="0" max="100" onChange={this.handleChange} />
          <div>Current Value: {this.state.displayValue}</div>
        </div>
      );
    },
  });

  ReactDOM.render(<App />, document.getElementById('root'));
