import React from 'react'
import Slider from '../../Slider/Slider'
import Button from '../../Button/Buttton'

const Shape = (props) => {

    setSize = size => {
        props.size = size;
    }
    return(
        <div>
            <Slider size = {this.setSize}>Size</Slider>
            <Button bttnFun = {this.setSize}>Circle</Button>
            <Button>Rectangle</Button>
        </div>
        );
    };

export default Shape;
