import React, {Component} from 'react'
import Aux from '../hoc/Aux'
import Toolbar from '../UI/ToolBar/Toolbar'
import Canvas from '../UI/Canvas/Canvas'
import SideDrawer from '../UI/SideDrawer/SideDrawer'
import Slider from 'react-rangeslider'
import './layout.css'

class Layout extends Component {
    state={
        sideDrawerOpen: false,
        currentTask:"",
        volume: 0
    }

    SideDrawerToggle =(props) => {
        this.setState({sideDrawerOpen: !this.state.sideDrawerOpen});
    }

    SideDrawerOpen = (props) => {
        this.setState({sideDrawerOpen: true});
    }

    ToolBarClickListner = (task) => {
        this.setState({currentTask: task});
    }


    handleChangeStart = () => {
        console.log('Change event started')
      };

    handleChange = value => {
        this.setState({
          volume: value
        })
      };

      handleChangeComplete = () => {
        console.log('Change event completed')
      };

    render(){
        return(
            <Aux>
                <SideDrawer open = {this.state.sideDrawerOpen} Task = {this.state.currentTask}/>
                <Toolbar 
                    sideDrawerToggle={this.SideDrawerToggle} 
                    toolOption={this.ToolBarClickListner} 
                    Task={this.state.currentTask}
                    ToolClicked = {this.SideDrawerOpen}/>
                <Canvas Task={this.state.currentTask}/>
                <div className='slider'> 
                    <Slider 
                        min={0}
                        max={100}
                        value={this.state.volume}
                        onChangeStart={this.handleChangeStart}
                        onChange={this.handleChange}
                        onChangeComplete={this.handleChangeComplete}/>
                </div>
             </Aux>
        );
    }
}
export default Layout;
