import React, {Component} from 'react'
import classes from './Canvas.css'
// import { NONAME } from 'dns';

class Canvas extends Component {
    state= {
        mousedown: false,
        startX: 0,
        startY: 0,
        endX: 0,
        endY: 0,
        one_state_back: null,
        current_state: null
    }

    componentDidMount(){
        const canvas = this.refs.canvas;
        const ctx = canvas.getContext("2d");
        const statei = ctx.getImageData(0,0,canvas.width,canvas.height);
        this.setState({one_state_back: statei, current_state: statei});
    }

    MouseDown = (e) => {
        e.persist();
        const statei = ctx.getImageData(0,0,c.width,c.height);
        this.setState(() => ({mousedown : true, startX: e.clientX, startY: e.clientY, one_state_back: statei}));
        this.activationFunction();
    }

    MouseMove = (e) => {
        e.persist();
        if(this.state.mousedown)
        {
            if(this.props.Task == "Line" || this.props.Task == "Circle")
            {
                this.clearCanvas();
            }
            this.activationFunction();
        }
    }

    MouseUp = (e) => {
        e.persist();
        this.setState(() => ({mousedown: false, endX: e.clientX, endY: e.clientY}));
        this.activationFunction();
    }

    activationFunction = () => {
        if(this.props.Task === "Line")
        {
            return(console.log("Line"));
        }
        else if(this.props.Task === "Pencil")
        {
            return(console.log("Pencil"));
        }
        else if(this.props.Task === "Shape")
        {
            return(console.log("Shape"));
        }
        else if(this.props.Task === "Erase")
        {
            return(console.log("Erase"));
        }
    }

    clearCanvas = () => {
        this.setState({current_state : one_state_back});
        this.ctx.clearRect(0,0,this.canvas.width,this.canvas.height);
        this.ctx.putImageData(this.state.current_state,0,0);	
    }

 render(){

    return(<canvas 
            ref = "canvas" 
            className= {classes.Canvas} 
            onMouseDown ={this.MouseDown} 
            onMouseMove= {this.MouseMove} 
            onMouseUp={this.MouseUp} ></canvas>);

    }   

}
export default Canvas;
