import React from 'react'
import Button from '../Button/Buttton'
import classes from './Toolbar.module.css'
import PencilLogo from '../../assets/pencil.png'
import DrawerToggle from '../drawerToggle/drawerToggle'

class Toolbar {
    ToolSelect = (Tool) => {
        props.toolOption(Tool);
    }
    render(){
        return(
        <header className = {classes.toolbar}>
            <div className = {classes.DrawerToggle}>
                <DrawerToggle clicked = {props.sideDrawerToggle}/>
            </div>
            <div className = {classes.Tools}>
                <Button bttnFun= {this.ToolSelect("Line")}>Line</Button>
                <Button bttnFun= {this.ToolSelect("Pencil")}>Pencil</Button>
                <Button bttnFun= {this.ToolSelect("Shape")}>Shape</Button>
                <Button bttnFun= {this.ToolSelect("Erase")}>Erase</Button>
            </div>
            <div>
                <a></a>
            </div>

        </header>
        );
    }
}


export default Toolbar;
