import React, {Component} from 'react'
import Slider from '../../Slider/Slider'
import Button from '../../Button/Buttton'

class Shape extends Component {
    state = {
        size : 1,
        task : ""
    }
    setSize = s => {
        this.setState({size: s});
    }

    setTask = t => {
        this.setState({task: t});
    }

    render(){
    return(
        <div>
            <Slider size = {this.setSize}>Size</Slider>
            <Button bttnFun = {this.setTask("Circle")}>Circle</Button>
            <Button bttnFun = {this.setTask("Rectangle")}>Rectangle</Button>
        </div>
        );
    }
    };

export default Shape;
