import './App.css';

class App extends Component{

    constructor(props) {
        super(props);
        this.funStartHandeller = this.funStartHandeller.bind(this);
        this.funMoveHandeller = this.funMoveHandeller.bind(this);
        this.funEndHeandeller = this.funEndHeandeller.bind(this);
    }

    state = {
        task : "Nan",
        start_point:{X:0,Y:0},
        end_point:{X:0,Y:0},
        mouseDown: false
    }

    componentDidMount(){
        this.canvas= document.getElementById("myCanvas");
        this.ctx = this.canvas.getContext("2d");
    }

    ActivationFuncHandeller = () => {
        if(this.state.task === "Line")
        {
            console.log("Line");
        }
        if(this.state.task === "Circle")
        {
            console.log("Circle");
        }
        if(this.state.task === "Erase")
        {
            console.log("Erase");
        }
        if(this.state.task === "HandLine")
        {
            console.log("Handline");
        }
    }

    handLineHandeller(event)
		{
			// this.state.start_point.X = end_point.X;
			// start_point.Y = end_point.Y;
			// end_point.X = event.clientX;
			// end_point.Y = event.clientY;
			this.ctx.beginPath();
			this.ctx.moveTo(start_point.X,start_point.Y);
			ctx.lineTo(end_point.X,end_point.Y);
			ctx.stroke();
			current_state = ctx.getImageData(0,0,c.width,c.height);
		}


    currentEventHandeller = (tsk) => {
        this.setState( {task : tsk }, () => console.log(this.state.task) );
    }

    funStartHandeller = (event) => {
        // console.log("Hekloisdhcosid",event.clientX,event.clientY);
        this.ActivationFuncHandeller();
    }

    funMoveHandeller = (event) => {
        // console.log("asdjeij",event.clientX,event.clientY);
        this.ActivationFuncHandeller();
    }

    funEndHeandeller = (event) => {
        // console.log("sdcsdocijs",event.clientX,event.clientY);
        this.ActivationFuncHandeller();
    }


    render(){

        // if(this.state.task)
        // {

        // }
        // activationFunction = (task,event,start_point,end_point) => {
        //     if(task == "Line")
        //     {
        //         return drawLine(event,start_point,end_point);
        //     }
        //     else if(task == "Circle")
        //     {
        //         return drawCircle(event,start_point,end_point);
        //     }
        //     else if(task == "Erase")
        //     {
        //         return Eraser(event);
        //     }
        //     else
        //     {
        //         return handLine(event,start_point,end_point);
        //     }
        // }


    return(
            <div>
                <button onClick= {() => this.currentEventHandeller("Line")}> Line</button>
                <button onClick= {() => this.currentEventHandeller("Circle")}> Circle</button>
                <button onClick= {() => this.currentEventHandeller("Erase")}> Erase</button>
                <button onClick= {() => this.currentEventHandeller("HandLine")}> Handline</button>
                <div>
                <canvas id ="myCanvas" className ="DrawingArea" 
                    onMouseDown= { this.funStartHandeller} 
                    onMouseMove = { this.funMoveHandeller} 
                    onMouseUp= { this.funEndHeandeller} > </canvas>
                </div>  
            </div>
        );
    }

}

export default App;
